import React from 'react';
import { Box, Heading, Text, Image, StarIcon, Badge, PseudoBox } from '@chakra-ui/core';

const CharacterItem = props => {
  const { id, name, status, species, type, gender, image } = props;

  const statusColors = {
    Alive: 'teal',
    Dead: 'red',
    unknown: 'purple'
  };

  return (
    <PseudoBox
      maxW="sm"
      borderWidth="1px"
      rounded="lg"
      overflow="hidden"
      shadow="md"
      _active={{
        bg: '#dddfe2',
        transform: 'scale(0.98)',
        borderColor: '#bec3c9'
      }}
    >
      <Image src={image} alt={name} width="100%" />

      <Box p="6">
        <Box d="flex" alignItems="baseline">
          <Badge rounded="full" px="2" variantColor={statusColors[status]}>
            {status}
          </Badge>
          <Box
            color="gray.500"
            fontWeight="semibold"
            letterSpacing="wide"
            fontSize="xs"
            textTransform="uppercase"
            ml="2"
          >
            dawdawdawdaw
          </Box>
        </Box>

        <Box mt="1" fontWeight="semibold" as="h4" lineHeight="tight" isTruncated>
          {name}
        </Box>

        <Box>
          123123
          <Box as="span" color="gray.600" fontSize="sm">
            / wk
          </Box>
        </Box>

        <Box d="flex" mt="2" alignItems="center">
          <Box as="span" ml="2" color="gray.600" fontSize="sm">
            3 reviews
          </Box>
        </Box>
      </Box>
    </PseudoBox>
  );
};

export default CharacterItem;
