import React from 'react';
import { Button, Box } from '@chakra-ui/core';

const Pagination = ({ next, prev, onNext, onPrev, pages }) => {
  const currentPage = prev ? prev + 1 : next - 1;

  return (
    <Box mt="20px" mb="20px">
      {prev && (
        <Button size="xs" leftIcon="arrow-back" onClick={onPrev}>
          {prev}
        </Button>
      )}
      <Button
        size="xs"
        marginLeft="5px"
        marginRight="5px"
        disabled
      >{`${currentPage}/${pages}`}</Button>
      {next && (
        <Button size="xs" rightIcon="arrow-forward" onClick={onNext}>
          {next}
        </Button>
      )}
      {/* {`${pages} pages...`} */}
    </Box>
  );
};

export default Pagination;
