import React from 'react';
import {
  Box,
  Heading,
  Text,
  Image,
  StarIcon,
  Badge,
  PseudoBox,
  Button,
  Tooltip,
  Icon,
  Divider
} from '@chakra-ui/core';
import { FaRegDizzy, FaRegFlushed, FaRegGrin } from 'react-icons/fa';

const CharacterItem = props => {
  const { id, name, status, species, type, gender, image } = props;

  const statusMap = {
    Alive: {
      color: 'green',
      icon: FaRegGrin
    },
    Dead: {
      color: 'red',
      icon: FaRegDizzy
    },
    unknown: {
      color: 'purple',
      icon: FaRegFlushed
    }
  };

  return (
    <PseudoBox
      maxW="sm"
      borderWidth="1px"
      rounded="lg"
      overflow="hidden"
      shadow="md"
      transition="0.3s all"
      _hover={{
        transform: 'translateY(-5px)'
      }}
    >
      <Image src={image} alt={name} width="100%" />

      <Box p="6">
        <Heading as="h2" mb="10px" isTruncated size="md">
          {name}
        </Heading>

        <Divider />

        <Box d="flex" alignItems="center" mb="10px">
          <Tooltip
            hasArrow
            label={`Status: ${status}`}
            placement="top"
            shouldWrapChildren
            display="block"
            color={statusMap[status].color}
          >
            <Box
              display="inline-block"
              w="15px"
              as={statusMap[status]['icon']}
              bg={statusMap[status]['color']}
            />
          </Tooltip>
          <Box
            color="gray.500"
            fontWeight="semibold"
            letterSpacing="wide"
            fontSize="xs"
            textTransform="uppercase"
            ml="2"
          >
            &bull; {species}
          </Box>
        </Box>

        <Box d="flex" alignItems="baseline">
          <Badge rounded="full" px="2" variantColor={statusMap[status]['color']}>
            {status === 'unknown' ? 'Status unknown' : status}
          </Badge>
        </Box>

        <Box>
          123123
          <Box as="span" color="gray.600" fontSize="sm">
            / wk
          </Box>
        </Box>

        <Box d="flex" mt="2" alignItems="center">
          <Box as="span" ml="2" color="gray.600" fontSize="sm">
            3 reviews
          </Box>
        </Box>
        <Button rightIcon="arrow-forward" mt="20px" width="100%" variantColor="teal" size="md">
          Details
        </Button>
      </Box>
    </PseudoBox>
  );
};

export default CharacterItem;
