import React from 'react';
import { Box, Link, PseudoBox, Heading, useColorMode, Button } from '@chakra-ui/core';
import { FaRadiationAlt, FaGithub } from 'react-icons/fa';

const Navbar = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  return (
    <PseudoBox
      borderBottom="1px solid blue.500"
      borderColor="blue.300"
      shadow="md"
      paddingTop="30px"
      paddingBottom="30px"
      paddingLeft={{ md: '50px', sm: '40px', xs: '20px' }}
      paddingRight={{ md: '50px', sm: '40px', xs: '20px' }}
    >
      <Box d="flex" alignItems="center" justifyContent="space-between" flexDir="row">
        <Link href="https://github.com/VictorLandim/rick-and-morty-wiki">
          <Box size={28} as={FaGithub} />
        </Link>
        <Link href="/">
          <Box d="flex" alignItems="center" flexDir="row">
            <Box as={FaRadiationAlt} mr="10px" />
            <Heading fontSize="md">Rick and Morty Wiki</Heading>
          </Box>
        </Link>
        <Button onClick={toggleColorMode}>Toggle {colorMode === 'light' ? 'Dark' : 'Light'}</Button>
      </Box>
    </PseudoBox>
  );
};

export default Navbar;
