import React, { useState } from 'react';
import { useQuery } from '@apollo/react-hooks';
import { useRouter } from 'next/router';
import { gql } from 'apollo-boost';
import {
  Box,
  Heading,
  SimpleGrid,
  Text,
  Tab,
  Tabs,
  TabList,
  TabPanel,
  TabPanels,
  Image,
  Button
} from '@chakra-ui/core';
import { GET_CHARACTER_DETAILS } from '../../lib/queries';
import Loader from '../../components/common/Loader';
import { Error } from '../CharacterList/styles';

const CharacterDetails = () => {
  const router = useRouter();

  const { pid } = router.query;

  const { loading, error, data } = useQuery(GET_CHARACTER_DETAILS(gql), {
    variables: { id: pid },
    notifyOnNetworkStatusChange: true,
    fetchPolicy: 'cache-first'
  });

  if (loading) return <Loader />;
  if (error) return <Error error="error" message="Erro fetching character data..." />;

  const { name, image, status, species, type, gender, origin, location, episode } = data[
    'character'
  ];

  const renderEpisodes = () => episode.map(episode => <p>{JSON.stringify(episode)}</p>);

  return (
    <>
      <Button size="sm" leftIcon="arrow-back" onClick={onPrev}>
        {prev}
      </Button>
      <Box
        padding={{ md: '20px', sm: '20px', xs: '20px' }}
        marginLeft={{ md: '150px', sm: '20px', xs: '20px' }}
        marginRight={{ md: '150px', sm: '20px', xs: '20px' }}
        marginTop="50px"
        borderRadius="10px"
        d="flex"
        alignItems={{ md: 'flex-start', sm: 'center', xs: 'center' }}
        justifyContent="normal"
        flexDir={{ md: 'row', sm: 'column', xs: 'column' }}
      >
        <Box position="relative" flex="0 0 40%" mb="100px">
          <Image
            src={image}
            alt={name}
            borderRadius="20px 20px 0px 0px"
            textAlign="center"
            width="100%"
          />
          <Box p="20px" bg="rgba(0,0,0,0.75)" position="absolute" bottom="0" w="100%">
            <Heading color="white">{name}</Heading>
          </Box>
        </Box>
        <Box pl="20px" pr="20px" flex="1">
          <Tabs isFitted>
            <TabList mb="1em">
              <Tab>Info</Tab>
              <Tab>Episodes</Tab>
            </TabList>
            <TabPanels>
              <TabPanel>
                <p>one!</p>
              </TabPanel>

              <TabPanel>{renderEpisodes()}</TabPanel>
            </TabPanels>
          </Tabs>
        </Box>
      </Box>
    </>
  );

  return <pre>{JSON.stringify(data, null, 2)}</pre>;
};

export default CharacterDetails;
