import React, { useState } from 'react';
import { useQuery } from '@apollo/react-hooks';
import { useRouter } from 'next/router';
import { gql } from 'apollo-boost';
import {
  Box,
  Heading,
  SimpleGrid,
  Text,
  Tab,
  Tabs,
  TabList,
  TabPanel,
  TabPanels
} from '@chakra-ui/core';
import { GET_CHARACTER_DETAILS } from '../../lib/queries';
import Loader from '../../components/common/Loader';
import { Error } from '../CharacterList/styles';

const CharacterDetails = () => {
  const router = useRouter();

  const { pid } = router.query;

  const { loading, error, data } = useQuery(GET_CHARACTER_DETAILS(gql), {
    variables: { id: pid },
    notifyOnNetworkStatusChange: true,
    fetchPolicy: 'cache-first'
  });

  if (loading) return <Loader />;
  if (error) return <Error error="error" message="Erro fetching character data..." />;

  return (
    <Box
      padding={{ md: '20px', sm: '20px', xs: '20px' }}
      marginLeft={{ md: '150px', sm: '20px', xs: '20px' }}
      marginRight={{ md: '150px', sm: '20px', xs: '20px' }}
      marginTop="50px"
      shadow="sm"
      borderRadius="10px"
      d="flex"
      alignItems="center"
      justifyContent="center"
      flexDir="row"
    >
      <Box></Box>
      <Box>
        <Tabs isFitted variant="enclosed">
          <TabList mb="1em">
            <Tab>Info</Tab>
            <Tab>Locations</Tab>
            <Tab>Episodes</Tab>
          </TabList>
          <TabPanels>
            <TabPanel>
              <p>one!</p>
            </TabPanel>
            <TabPanel>
              <p>two!</p>
            </TabPanel>

            <TabPanel>
              <p>3!</p>
            </TabPanel>
          </TabPanels>
        </Tabs>
      </Box>
    </Box>
  );

  return <pre>{JSON.stringify(data, null, 2)}</pre>;
};

export default CharacterDetails;
