import React from 'react';
import { Box, Heading, Text, Image, StarIcon, Badge, PseudoBox, Button } from '@chakra-ui/core';

const CharacterItem = props => {
  const { id, name, status, species, type, gender, image } = props;

  const statusColors = {
    Alive: 'teal',
    Dead: 'red',
    unknown: 'purple'
  };

  return (
    <PseudoBox
      maxW="sm"
      borderWidth="1px"
      rounded="lg"
      overflow="hidden"
      shadow="md"
      transition="0.3s all"
      _hover={{
        transform: 'translateY(-3px)'
      }}
    >
      <Image src={image} alt={name} width="100%" />

      <Box p="6">
        <Heading as="h2" mb="20px" isTruncated>
          {name}
        </Heading>

        <Box d="flex" alignItems="baseline">
          <Badge rounded="full" px="2" variantColor={statusColors[status]}>
            {status}
          </Badge>
          <Box
            color="gray.500"
            fontWeight="semibold"
            letterSpacing="wide"
            fontSize="xs"
            textTransform="uppercase"
            ml="2"
          >
            {species}
          </Box>
        </Box>

        <Box>
          123123
          <Box as="span" color="gray.600" fontSize="sm">
            / wk
          </Box>
        </Box>

        <Box d="flex" mt="2" alignItems="center">
          <Box as="span" ml="2" color="gray.600" fontSize="sm">
            3 reviews
          </Box>
        </Box>
        <Button rightIcon="arrow-forward" mt="20px" width="100%" variantColor="teal" size="md">
          Details
        </Button>
      </Box>
    </PseudoBox>
  );
};

export default CharacterItem;
