import React from 'react';
import {
  Box,
  Heading,
  Text,
  Image,
  StarIcon,
  Badge,
  PseudoBox,
  Button,
  Tooltip,
  Icon,
  Divider
} from '@chakra-ui/core';
import { FaRegDizzy, FaRegFlushed, FaRegGrin, FaMapMarkerAlt } from 'react-icons/fa';

const CharacterItem = props => {
  const { id, name, status, species, type, gender, image, location } = props;

  const statusMap = {
    Alive: {
      color: 'teal.500',
      icon: FaRegGrin
    },
    Dead: {
      color: 'red.500',
      icon: FaRegDizzy
    },
    unknown: {
      color: 'purple.500',
      icon: FaRegFlushed
    }
  };

  return (
    <PseudoBox
      maxW="sm"
      borderWidth="1px"
      rounded="lg"
      overflow="hidden"
      shadow="md"
      transition="0.3s all"
      _hover={
        {
          // transform: 'translateY(-5px)'
        }
      }
    >
      <Image src={image} alt={name} width="100%" />

      <Box p="6">
        <Heading as="h2" mb="3px" isTruncated size="md">
          {name}
        </Heading>

        <Box
          color="teal.400"
          fontWeight="semibold"
          letterSpacing="wide"
          fontSize="xs"
          textTransform="uppercase"
        >
          {species}
        </Box>

        <Divider />

        <Box d="flex" alignItems="center" mb="10px">
          <Tooltip
            hasArrow
            label={`Status: ${status}`}
            placement="top"
            shouldWrapChildren
            display="block"
            bg={statusMap[status]['color']}
          >
            <Box display="inline-block" w="15px" as={statusMap[status]['icon']} />
          </Tooltip>
        </Box>

        <Box as="span" color="gray.600" fontSize="sm" as={FaMapMarkerAlt}></Box>

        <Box as="span" color="gray.600" fontSize="sm">
          {location.name}
        </Box>

        <Box d="flex" mt="2" alignItems="center">
          <Box as="span" ml="2" color="gray.600" fontSize="sm">
            3 reviews
          </Box>
        </Box>
        <Button rightIcon="arrow-forward" mt="20px" width="100%" variantColor="teal" size="md">
          Details
        </Button>
      </Box>
    </PseudoBox>
  );
};

export default CharacterItem;
