import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import { gql } from 'apollo-boost';
import { Box, Heading, Stack, Flex, SimpleGrid } from '@chakra-ui/core';
import CharacterItem from '../CharacterItem';
import Loader from '../Loader';
import Filter from '../Filter';
import { FaSadTear } from 'react-icons/fa';

// location {
//   id
//   name
//   type
// }

// origin {
//   id
//   name
//   type
//   dimension
// }

const GET_CHARACTERS = gql`
  {
    characters(limit: 1000) {
      results {
        id
        name
        status
        species
        type
        gender

        image
        episode {
          id
          name
          air_date
        }

        location {
          name
        }
        created
      }
    }
  }
`;

const Error = () => (
  <Box d="flex">
    <Heading mt="50px" color="gray.400" textAlign="center" fontSize="lg" fontWeight="semibold">
      Could not fetch Rick and Morty Characters :(
    </Heading>

    <Box as={FaSadTear} />
  </Box>
);

const CharacterList = () => {
  const { loading, error, data, fetchMore } = useQuery(GET_CHARACTERS);
  const characterData = data ? data['characters']['results'] : [];

  if (loading) return <Loader />;

  return (
    <Box p={{ md: '50px', sm: '40px', xs: '20px' }}>
      <Heading as="h1" size="2xl">
        Rick and Morty Characters:
      </Heading>

      {error ? (
        <Error />
      ) : (
        <>
          <Filter />

          <SimpleGrid columns={{ md: 5, sm: 3, xs: 1 }} spacingX="30px" spacingY="30px">
            {characterData.map(character => (
              <CharacterItem key={character.id} {...character} />
            ))}
          </SimpleGrid>
        </>
      )}
    </Box>
  );
};

export default CharacterList;
